import React, { useEffect } from "react";
import clsx from "clsx";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import Drawer from "@material-ui/core/Drawer";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import List from "@material-ui/core/List";
import CssBaseline from "@material-ui/core/CssBaseline";
import Typography from "@material-ui/core/Typography";
import Divider from "@material-ui/core/Divider";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import ChevronLeftIcon from "@material-ui/icons/ChevronLeft";
import ChevronRightIcon from "@material-ui/icons/ChevronRight";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";
import HomePage from "./pages/home/Home";
import UserPage from "./pages/user/User";
import ProductPage from "./pages/product/Product";
import CategoryPage from "./pages/category/Category";
import InfoUser from "./pages/infoUser/InfoUser";
import InfoProduct from "./pages/infoProduct/InfoProduct";
import CreateProduct from "./pages/createProduct/CreateProduct";
import {
  Switch,
  Route,
  BrowserRouter as Router,
  Redirect,
  useHistory,
} from "react-router-dom";
import {
  Home,
  Person,
  Smartphone,
  Category,
  NotificationsNone,
  Language,
  Settings,
} from "@material-ui/icons";
import "./App.css";
import { Container } from "@material-ui/core";
import Login from "./pages/login/Login";
import CreateCategory from "./pages/createCategory/CreateCategory";
import { Dropdown } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { useSelector, useDispatch } from "react-redux";
import { signout } from "./redux/actions/userActions";
import { toast, ToastContainer } from 'react-toastify';

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(["width", "margin"], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: 36,
  },
  hide: {
    display: "none",
  },
  drawer: {
    width: drawerWidth,
    flexShrink: 0,
    whiteSpace: "nowrap",
  },
  drawerOpen: {
    width: drawerWidth,
    transition: theme.transitions.create("width", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerClose: {
    transition: theme.transitions.create("width", {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    overflowX: "hidden",
    width: theme.spacing(7) + 1,
    [theme.breakpoints.up("sm")]: {
      width: theme.spacing(9) + 1,
    },
  },
  toolbar: {
    display: "flex",
    alignItems: "center",
    justifyContent: "flex-end",
    padding: theme.spacing(0, 1),
    // necessary for content to be below app bar
    ...theme.mixins.toolbar,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
}));

export default function MiniDrawer() {
  const classes = useStyles();
  const theme = useTheme();
  const [open, setOpen] = React.useState(false);

  const handleDrawerOpen = () => {
    setOpen(true);
  };

  const handleDrawerClose = () => {
    setOpen(false);
  };

  const userSignin = useSelector((state) => state.userSignin);
  const { userInfo, success } = userSignin;

  useEffect(() => {
    if(success == true) {
      toast.success("Login successful!");
    }
    if(success == false) {
      toast.error("Incorrect username or password!");
    }
  }, [success]);

  const dispatch = useDispatch();
  const history = useHistory();

  const signoutHandler = () => {
    dispatch(signout());
    toast.success("Logout successful!");
  };

  return (
    <Router>
      <Switch>
        <Route exact path="/login">
          {userInfo ? <Redirect to="/" /> : <Login />}
        </Route>
        {!userInfo ? <Redirect to="/login"/> : (
          <div className={classes.root}>
            <CssBaseline />
            <AppBar
              position="fixed"
              className={clsx(classes.appBar, {
                [classes.appBarShift]: open,
              })}
            >
              <Toolbar className="topbar">
                <div className="title">
                  <IconButton
                    color="inherit"
                    aria-label="open drawer"
                    onClick={handleDrawerOpen}
                    edge="start"
                    className={clsx(classes.menuButton, {
                      [classes.hide]: open,
                    })}
                  >
                    <MenuIcon />
                  </IconButton>
                  <Typography variant="h6" noWrap>
                    TgddAdmin
                  </Typography>
                </div>
                <div className="topRight">
                  {userInfo ? <p>Welcome, {userInfo.username}</p> : <></>}
                  <div className="topbarIconContainer">
                    <NotificationsNone />
                  </div>
                  <div className="topbarIconContainer">
                    <Language />
                  </div>
                  <div className="topbarIconContainer">
                    <Dropdown>
                      <Dropdown.Toggle id="dropdown-basic">
                        <Settings className="iconSetting" />
                      </Dropdown.Toggle>
                      <Dropdown.Menu>
                        <Dropdown.Item onClick={signoutHandler}>
                          Logout
                        </Dropdown.Item>
                        <Dropdown.Item>Edit profile</Dropdown.Item>
                      </Dropdown.Menu>
                    </Dropdown>
                  </div>
                  <img
                    src="data:image/png;base64,"
                    alt="avatar"
                    className="topAvatar"
                  />
                </div>
              </Toolbar>
            </AppBar>
            <Drawer
              variant="permanent"
              className={clsx(classes.drawer, {
                [classes.drawerOpen]: open,
                [classes.drawerClose]: !open,
              })}
              classes={{
                paper: clsx({
                  [classes.drawerOpen]: open,
                  [classes.drawerClose]: !open,
                }),
              }}
            >
              <div className={classes.toolbar}>
                <IconButton onClick={handleDrawerClose}>
                  {theme.direction === "rtl" ? (
                    <ChevronRightIcon />
                  ) : (
                    <ChevronLeftIcon />
                  )}
                </IconButton>
              </div>
              <Divider />
              <List>
                <a href="/" className="link">
                  <ListItem>
                    <ListItemIcon>
                      <Home />
                    </ListItemIcon>
                    <ListItemText>Home</ListItemText>
                  </ListItem>
                </a>
                <a href="/user" className="link">
                  <ListItem>
                    <ListItemIcon>
                      <Person />
                    </ListItemIcon>
                    <ListItemText>User</ListItemText>
                  </ListItem>
                </a>
                <a href="/product" className="link">
                  <ListItem>
                    <ListItemIcon>
                      <Smartphone />
                    </ListItemIcon>
                    <ListItemText>Product</ListItemText>
                  </ListItem>
                </a>
                <a href="/category" className="link">
                  <ListItem>
                    <ListItemIcon>
                      <Category />
                    </ListItemIcon>
                    <ListItemText>Category</ListItemText>
                  </ListItem>
                </a>
              </List>
            </Drawer>
            <main className={classes.content}>
              <div className={classes.toolbar} />
              <ToastContainer />
              <Route exact path="/">
                <HomePage />
              </Route>
              <Route exact path="/user">
                <Container>
                  <UserPage />
                </Container>
              </Route>
              <Route exact path="/product">
                <Container>
                  <ProductPage />
                </Container>
              </Route>
              <Route exact path="/category">
                <Container>
                  <CategoryPage />
                </Container>
              </Route>
              <Route exact path="/user/:userId">
                <Container>
                  <InfoUser />
                </Container>
              </Route>
              <Route exact path="/product/:productId">
                <Container>
                  <InfoProduct />
                </Container>
              </Route>
              <Route exact path="/createProduct">
                <Container>
                  <CreateProduct />
                </Container>
              </Route>
              <Route exact path="/category/createCategory">
                <Container>
                  <CreateCategory />
                </Container>
              </Route>
            </main>
          </div>
        )}
      </Switch>
    </Router>
  );
}
